var currentIndex = 0, slides = $('.slide'), slidesLength = slides.length;
function cycleSlides(current) { 
    var slide = $('.slide').eq(current);
    slides.hide();
    slide.show();
}
cycleSlides(currentIndex);
$(document).ready(function(){
    $('.v4-btn-prev').hide(); 
    $('.v4-btn-next').on('click', function() {
        $('.v4-btn-prev').show();
        currentIndex += 1;
        cycleSlides(currentIndex);
        if(currentIndex == 4) {
            if ($(".kbaru-modal").length > 0) {
                $(".kbaru-modal").remove();
            }
            return false;
        }
        if (currentIndex > slidesLength - 2) {
            $('.v4-btn-next').html('Complete Data Now >>');
            $('.v4-btn-next').addClass('btn-landing-next-completed');
        } else {
            $('.v4-btn-next').removeClass('btn-landing-next-completed');
            $('.v4-btn-next').attr("disabled", false);
        }
        cycleSlides(currentIndex);
    });
    $('.v4-btn-prev').on('click', function() {
        $('.v4-btn-next').attr("disabled", false);
        currentIndex -= 1;
        if (currentIndex <= 0) {
            $('.v4-btn-prev').hide();
        } else if (currentIndex >= slidesLength - 2) {
            $('.v4-btn-next').html('Next >');
        }
        cycleSlides(currentIndex);
    });
    $(".btn-landing-next-completed").on("click", function(){
        if ($(".kbaru-modal").length > 0) {
            $(".kbaru-modal").remove();
        }
        return false;
    });
});